#ifndef _CORE_OBJECT_H_
#define _CORE_OBJECT_H_
#include <Core/Reference.h>

#define APPLICATION_KEY_SIZE 4096
#define APPLICATION_OWNER_KEY "Meow Framework Version 1.0 (C++) <meow@unconfigured.org>"
#define APPLICATION_KEY_VERSION "GnuPG v1"
#define APPLICATION_PUBLIC_KEY ""
#define APPLICATION_PRIVATE_KEY ""

namespace Meow {
	namespace Core {
		class Object;
		typedef Reference<Object> ptr;
		typedef Reference<Object> Object_ptr;

		struct NameTracker
		{
			char * classname;
			struct NameTracker * next;
		};

		class Object
		{
		protected:
			virtual void SetMeowClassName( std::string );
		public:
			Object();
			virtual ~Object();
			virtual std::string GetMeowClassName();

			void Incref();
			void Decref();
			unsigned long GetRefCount();
			unsigned long Count();

			bool   IsInstanceOf( std::string classname );

			void Pause( long milliseconds );
			void Clone( Object * other );
			unsigned long GetProcessID();
			static ptr Null();
		private:
			unsigned long refcount;
			void        * darea;
			struct NameTracker * ___names;
		}; // End of Class Object

		std::string GetHardwareKey();
	}
} // End of NameSpace Meow

#endif
